/**
 * @file FileTypes.d.ts
 * @desc This file contains type declarations for various file types. Used for .css with tsx files.
 */

declare module '*.png' {
  const content: string;
  export default content;
}

declare module '*.jpg' {
  const content: string;
  export default content;
}

declare module '*.jpeg' {
  const content: string;
  export default content;
}

declare module '*.gif' {
  const content: string;
  export default content;
}